--
-- Cấu trúc bảng cho bảng `product`
--

CREATE TABLE `product` (
  `id` int(10) NOT NULL,
  `image_menu` varchar(200) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `title_menu` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `video` varchar(500) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image_product1` varchar(200) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image_product2` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
  `image_product3` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_product` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_product1` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title_product2` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `docon` varchar(10) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `chai` int(10) DEFAULT NULL,
  `ml` int(10) DEFAULT NULL,
  `content` longtext COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `chungloai` text COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `type_of_product` int(1) NOT NULL,
  `id_category` int(11) NOT NULL,
  `created_at` datetime DEFAULT NULL,
  `updated_at` datetime DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

