INSERT INTO `product` (`id`, `image_menu`, `title_menu`, `video`, `image_product1`, `image_product2`, `image_product3`, `title_product`, `title_product1`, `title_product2`, `docon`, `chai`, `ml`, `content`, `chungloai`, `type_of_product`, `id_category`, `created_at`, `updated_at`) VALUES
(1, 'https://www.sabeco.com.vn/Data/Sites/1/media/icon/saigon-special.png', 'BIA SAIGON SPECIAL', 'https://stsbcwebsitemedia.blob.core.windows.net/media/sabeco_sg_special-1-190809.mp4', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/special----2.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/special---5.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/special---4.jpg', 'Bia Saigon Special', 'Làm từ 100% lúa mạch mùa xuân', 'Bia Saigon Special Làm từ 100% lúa mạch mùa xuân', '4.9', 20, 355, 'Chỉ những bông lúa mạch mạnh mẽ nhất, phong dưỡng vươn qua mùa đông khắc nghiệt, nở bùng vào mùa xuân mới đem tới một hương vị bia cực kỳ hảo hạng.', 'Bia Lager.', 1, 2, '2021-08-20 15:57:22', '2021-08-29 16:34:59'),
(2, 'https://www.sabeco.com.vn/Data/Sites/1/media/icon/sai-gon-export.png', 'BIA SAIGON EXPORT1', 'https://stsbcwebsitemedia.blob.core.windows.net/media/sabeco_sg_export-3---190813.mp4', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---111.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---9.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/export---8.jpg', 'Bia Saigon Export ', 'Công thức nguyên bản', 'Bia Saigon Export – Công thức nguyên bản', '4.9', 20, 355, 'Ra đời từ năm 1875, trường tồn cùng thành phố với một hương vị đắm say, luôn đứng vững trong lòng người yêu bia Việt Nam', 'Bia Lager.', 1, 2, '2021-08-20 18:29:30', '2021-08-28 18:13:04'),
(8, 'https://www.sabeco.com.vn/Data/Sites/1/media/icon/saigon-larger.png', 'BIA SAIGON LAGER', 'khongco', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/lager---15.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/lager---14.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/lager---13.jpg', 'Bia Saigon Lager ', 'Huy chương Vàng bia quốc tế IBA 2019', 'Bia Saigon Lager Huy chương Vàng bia quốc tế IBA 2019', '4.3', 20, 355, '<p><span style=\"color: rgb(255, 255, 255); font-family: \"Open Sans\";\">Kết tinh của nghệ thuật nấu bia lâu đời với sự giám sát kỳ công của các bậc thầy nấu bia, tự hào đứng trong hàng ngũ những loại bia đạt Huy chương Vàng bia quốc tế IBA 2019.</span><br></p>', 'Bia Lager', 1, 2, '2021-08-25 11:27:29', '2021-08-28 18:26:04'),
(9, 'https://www.sabeco.com.vn/Data/Sites/1/media/new.png', '333', 'https://stsbcwebsitemedia.blob.core.windows.net/media/333-Credentials-45s-North-Midres.mp4', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/dsad.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/bia-333-cm.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/333-frame3.jpg', '333 Export', 'Bật lịch lãm Sáng tự tin', '3 TINH HOA – VỊ HUYỀN THOẠI', '5.3', 24, 330, '<span style=\"font-family: \"Open Sans\";\"><font color=\"#000000\">Là thương hiệu bia lon đầu tiên được sản xuất tại thị trường Việt Nam kể từ năm 1985. Bia 333 là thành quả kết hợp từ những gì tinh túy nhất của 3 quốc gia: với hoa bia Hallertau từ vùng Bavaria nước Đức, công thức trăm năm kế thừa từ Pháp và được ủ bởi tài năng và tinh thần của các nghệ nhân nấu bia Việt Nam. Chính các yếu tố đó đã đang mang lại hương bia nồng nàn và vị bia đầy đặn đặc trưng của Bia 333. Trải nghiệm vị Bia 333 là trải nghiệm vị bia huyền thoại đã không ngừng được kế thừa và gìn giữ suốt hơn 140 năm qua.</font></span><br>', 'Bia 333', 1, 2, '2021-08-26 09:42:40', '2021-08-28 18:40:04'),
(10, 'https://www.sabeco.com.vn/Data/Sites/1/media/thuong-hieu/lac-viet/sanpham52x81-menumega.png', 'Bia Lạc Việt', 'https://stsbcwebsitemedia.blob.core.windows.net/media/bia_lac_viet_6s_open-bottle_v01_200629_0200_nl.mp4', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/bannervideo.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/banner-final.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/banner_0408.jpg', 'Bia Lạc Việt', 'Công thức Lạc Việt', 'Bia Lạc Việt - Công thức Lạc Việt', '4.3', 24, 330, '<p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(255, 255, 255); font-family: \"Open Sans\";\">Công thức Thuần Việt Ẩn sâu trong thư viện của nhà máy BiaSaigon Nguyễn Chí Thanh là một kho tàng kiến thức cũng như nghiên cứu về bia của nhiều thế hệ các bậc thầy nấu bia Việt Nam xuyên suốt bề dày lịch sử 145 năm qua.</p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(255, 255, 255); font-family: \"Open Sans\";\">Để kỷ niệm dấu mốc thời gian đặc biệt này, các bậc thầy nấu bia tại SABECO đã quyết định sáng tạo ra một công thức tinh túy để đại diện xứng đáng cho di sản của thương hiệu. Phát kiến này đã cho một kết quả mỹ mãn. Một hương vị bia đầy say mê, vẫn đậm đà nhưng êm mượt và sảng khoái khiến cho những khoảnh khắc tận hưởng cùng bạn bè trở nên lâu hơn. </p>', 'Bia Lager', 1, 2, '2021-08-26 09:07:44', '2021-08-28 18:58:04'),
(11, 'https://www.sabeco.com.vn/Data/Sites/1/media/thuong-hieu/saigon-chill/sp-menu-mega-chill.png', 'Bia Saigon Chill', 'https://stsbcwebsitemedia.blob.core.windows.net/media/bia-saigon-chill-video.mp4', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/chill-banner-video.png', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/chill-hinh2.jpg', 'https://www.sabeco.com.vn/Data/Sites/1/Banner/chill-hinh3.jpg', 'Bia Saigon Chill', 'Không uống được bia hehehe', 'Công thức lên men lạnh âm 2 độ C', '4.6', 24, 330, '<p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(255, 255, 255); font-family: \"Open Sans\";\">Thật đáng tiếc khi những cuộc vui tới bến bên những người anh em bị gián đoạn bởi những lo lắng bên ngoài, tay thì bận rộn nhắn tin còn chân thì không thể đứng yên vì những cuộc gọi.<br style=\"box-sizing: inherit;\"></p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(255, 255, 255); font-family: \"Open Sans\";\">Giới thiệu Bia Saigon Chill được lên men lạnh ở âm 2 độ C, nhiệt độ cực thấp để giữ vị bia luôn tươi mới.  Cảm giác sảng khoái và hậu vị mát lạnh của Bia Saigon Chill sẽ đánh thức mọi giác quan của bạn, để từng ngụm bia đều mang đến sự sảng khoái tức thì, mang trọn vẹn con người bạn về tận hưởng cuộc vui.</p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 10px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(255, 255, 255); font-family: \"Open Sans\";\">Giờ đây bạn có thể gỡ bỏ những áp lực và sẵn sàng tận hưởng cuộc vui cùng anh em bạn bè một cách trọn vẹn với Bia Saigon Chill – Sảng khoái tột đỉnh, vui tột đỉnh.</p><div><br></div>', 'Bia Lager', 1, 2, '2021-08-26 09:22:48', '2021-08-28 18:06:05'),
(12, 'https://www.sabeco.com.vn/Data/Sites/1/media/thuong-hieu/h9.png', 'Chương Dương', 'Không có', 'https://www.sabeco.com.vn/Data/Sites/1/media/icon/chuong-duong.png', 'khong co', 'khònco', 'CÔNG TY CỔ PHẦN NƯỚC GIẢI KHÁT CHƯƠNG DƯƠNG', 'Nước giải khát có gaz', 'nƯỚC NGỌT', '0', 24, 330, '<p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><span style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: left;\">Tên viết tắt: CDBECO<br style=\"box-sizing: inherit;\"></span></p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><span style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: left;\">Mã chứng khoán: SCD (Sàn niêm yết: Sở Giao dịch Chứng khoán TP. HCM)</span></p><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">Trụ sở chính: 606 Võ Văn Kiệt, P. Cầu Kho, Quận 1, TP. HCM</p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">Điện thoại: (08) 3836.7518 – 3836.8747</p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">Fax: (08) 3836.7176</p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">Website:  www.cdbeco.com.vn <span class=\"Apple-tab-span\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: left; white-space: pre;\">	</span></p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">Chi nhánh:</p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: left;\">Bình Dương: D-5B, 5D, 5F-CN, KCN Mỹ Phước 3, xã Hòa Lợi, Bến Cát – Bình Dương.</span></p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: left;\">VITA: 577 Hùng Vương, P. 12, Quận 6, TP. HCM</span></p><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: left;\">Gò Dầu: 280 Gò Dầu, Phường Tân Quý, Quận Tân Phú, TP. HCM</span></p></div><p style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 15px; margin-left: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><span style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(192, 0, 0);\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">1. Nước giải khát có gaz:   <br style=\"box-sizing: inherit;\"></strong></span></p><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">1.1 Nước giải khát có gaz Sáxị</strong>  </div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Sản phẩm truyền thống của Chương Dương và là sản phẩm mang hương vị độc đáo của Việt Nam, được khách hàng rất ưa chuộng. Sản phẩm có thành phần quế và hồi hỗ trợ tiêu hóa, hô hấp, tuần hoàn, làm ấm cơ thể.</li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Thành phần: Nước CO2, đường, chất điều chỉnh độ acid: acid citric (330), hương Sáxị tự nhiên và tổng hợp, màu tự nhiên caramen nhóm 1 (150a), chất bảo quản: sodium benzoate (211). </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Chỉ tiêu chất lượng chủ yếu: hàm lượng đường 110 ±20g/l. </li></ul><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">1.2 Nước giải khát có gaz hương Cam</strong></div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Là sản phẩm thân thuộc với người tiêu dùng với hương vị cam tươi mát, vị thơm ngon, giúp giải khát hiệu quả trong các hoạt động thường ngày. </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Thành phần: Nước bảo hòa CO2, đường, chất điều chỉnh độ acid: acid citric (330), hương cam tự nhiên và tổng hợp, phẩm màu: sunset yellow (110), chất bảo quản: sodium benzoate (211). </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Chỉ tiêu chất lượng chủ yếu: hàm lượng đường tổng 125 ±10g/l. </li></ul><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">1.3 Nước giải khát có gaz hương Dâu</strong></div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Là sản phẩm thân thuộc và phù hợp với phái nữ. Với hương vị dâu thơm ngon, sản phẩm không thể thiếu trong các buổi tiệc họp mặt bạn bè.  </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Thành phần: Nước CO2, đường, chất điều chỉnh độ acid: acid citric (330), hương dâu tổng hợp, phẩm màu (129), chất bảo quản: sodium benzoate (211). </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Chỉ tiêu chất lượng chủ yếu: hàm lượng đường 124 ±10g/l. </li></ul><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">1.4 Nước giải khát có gaz Bạc hà</strong></div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Tinh chất bạc hà the mát đã tạo nên nét đặc trưng của sản phẩm. Xuất hiện khá lâu và gần như là duy nhất trên thị trường nước giải khát.</li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Thành phần: Nước CO2, đường, chất điều chỉnh độ acid: acid citric (330), hương bạc hà tổng hợp, tinh dầu bạc hà, màu thực phẩm tổng hợp: tartrazine (102), brilliant blue (133), chất bảo quản: sodium benzoate (211). </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Chỉ tiêu chất lượng chủ yếu: hàm lượng đường 120g/l. </li></ul><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">1.5 Nước giải khát có gaz Cream soda </strong></div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Với hương vị thơm ngon của kem tươi hòa quyện với soda, sản phẩm mang đến cảm giác mới lạ cho những ai lần đầu tiên thưởng thức. </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Thành phần: Nước CO2, đường, chất điều chỉnh độ acid: acid citric (330), hương cream soda tổng hợp, màu chiết xuất từ cây hồng hoa, chất bảo quản: sodium benzoate (211). </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Chỉ tiêu chất lượng chủ yếu: hàm lượng đường 125 ± 10g/l. </li></ul><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">1.6 Nước giải khát có gaz Soda</strong></div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Sản phẩm quen thuộc với vị đậm đà đặc trưng riêng, kết hợp chất gaz CO2 tinh khiết. Đặc biệt, dùng pha chế với chanh + đường, hoặc rượu để tạo nên những thức uống độc đáo theo khẩu vị riêng của mỗi người.</li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Thành phần: Nước bảo hòa CO2, chất ổn định: Sodium Bicacrbonate (500ii)..</li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Chỉ tiêu chất lượng chủ yếu: hàm lượng CO2 ≥ 6.8g/l </li></ul><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\"><span style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; color: rgb(192, 0, 0);\">2. Nước giải khát không gaz: <br style=\"box-sizing: inherit;\"><br style=\"box-sizing: inherit;\"></span></strong></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">2.1 Nước giải khát không gaz Nha Đam</strong></div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Nha Đam Chương Dương được làm từ nha đam nguyên chất với từng miếng nha đam tươi, không dùng phẩm màu và chất bảo quản, không gaz giúp bổ sung vitamin C. Sản phẩm thích hợp cho phái nữ.</li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Thành phần: Nước, đường, nha đam (5%), vitamin C, chất điều chình độ acid: acid citric (330), chất chống oxy hóa: acid ascorbic (300), hương nha đam tự nhiên và tổng hợp. </li><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Chỉ tiêu chất lượng chủ yếu: hàm lượng đường 110g/l, hàm lượng vitamin C 0.1g/l </li></ul><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><br style=\"box-sizing: inherit;\"></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; color: rgb(51, 51, 51); font-family: \"Open Sans\"; text-align: justify;\"><strong style=\"box-sizing: inherit; font-weight: bold; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent;\">2.2 Nước tinh khiết đóng chai Chương Dương:</strong></div><ul style=\"box-sizing: inherit; margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0px 20px; border: 0px; font-size: 15px; vertical-align: baseline; background: transparent; list-style-position: initial; list-style-image: initial; color: rgb(51, 51, 51); font-family: \"Open Sans\";\"><li style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; background: transparent; text-align: justify;\">Được khai thác từ nguồn nước ngầm qua xử lý bằng màng thẩm thấu ngược và thanh trùng bằng Ozon, tia cực tím (UV), NO3: ≤50mg/l ; NO2: ≤3mg/l. Tuân thủ theo đúng QCVN: 6-1:2010/BYT quy chuẩn kỹ thuật quốc gia về nước uống đóng chai của Bộ Y tế. Làm thêm sự sảng khoái và tươi trẻ.</li></ul>', 'Nước ngọt', 0, 2, '2021-08-26 17:35:21', '2021-08-28 16:24:30');

